    def randnum(self):
        _translate = QtCore.QCoreApplication.translate
        try:
            valu = rand.randint(int(self.leastn.value()), int(self.mostn.value()))
            self.numop.setDigitCount(len(str(valu)))
            self.numop.setProperty("value", valu)
            self.label_2.setText("Range")
        except:
            self.label_2.setText("Range Error!")
    def ht(self):
        if rand.randint(0, 1) == 1:
            self.htc.setText("Heads")
        else:
            self.htc.setText("Tails")
    def assci(self):
        _translate = QtCore.QCoreApplication.translate
        try:
            valu = rand.randint(int(self.leasta.value()), int(self.mosta.value()))
            self.astring.setText(chr(valu))
            self.label_7.setText(f'Range (Last int: {valu})')
        except:
            self.label_7.setText("Range Error!")
    def ct(self): self.Strings.clear()
    def stringytext(self):
        _translate = QtCore.QCoreApplication.translate
        try:
            stri = self.Strings.text()
            ar = stri.split()
            valu = rand.randint(0, len(ar)-1)
            self.stroutput.setText(ar[valu])
            self.label_6.setText(_translate("Form", "Items (Each seporated by spaces)"))
        except:
            self.label_6.setText(_translate("Form", "Items (Each seporated by spaces), Issue with Items!"))
